import "fmt"

func reverse(x int) int {
    result := 0

    for x != 0 {
        digit := x % 10
        x /= 10

        // 检查溢出情况
        if result > (1<<31-1)/10 || (result == (1<<31-1)/10 && digit > 7) {
            return 0
        }
        if result < (-1<<31)/10 || (result == (-1<<31)/10 && digit < -8) {
            return 0
        }

        result = result*10 + digit
    }

    return result
}

